<?php$block_title = trim($post_info_block_fields["infoblock_title"]);$block_subtitle = trim($post_info_block_fields["infoblock_subtitle"]);$block_with_heading = (($block_title != "" || $block_subtitle != "") ? true : false);$block_padding_top = (int)$post_info_block_fields["infoblock_padding_margin"]["infoblock_padding_top"];$block_padding_bottom = (int)$post_info_block_fields["infoblock_padding_margin"]["infoblock_padding_bottom"];$block_margin_top = (int)$post_info_block_fields["infoblock_padding_margin"]["infoblock_margin_top"];$block_margin_bottom = (int)$post_info_block_fields["infoblock_padding_margin"]["infoblock_margin_bottom"];$block_background_enable = $post_info_block_fields["infoblock_background_enable"];$block_background_image = $post_info_block_fields["infoblock_background_image"];$block_background_image_mobile = $post_info_block_fields["infoblock_background_image_mobile"];$block_background_color = strtolower($post_info_block_fields["infoblock_background_color"]);$block_dark_overlay = $post_info_block_fields["infoblock_dark_overlay"];$block_white_foreground = $post_info_block_fields["infoblock_white_foreground"];$albums_list = $post_info_block_fields["infoblock_albums_list"];$block_list_cols = (int)$post_info_block_fields["infoblock_cols"];$block_list_col_class = 'col-12';switch ($block_list_cols) {    case 1:        $block_list_col_class = 'col-12 col-sm-12 col-md-12 biglist-col-single';        break;    case 2:        $block_list_col_class = 'col-12 col-sm-6 col-md-6';        break;    case 3:        $block_list_col_class = 'col-12 col-sm-12 col-md-6 col-lg-4';        break;    case 4:        $block_list_col_class = 'col-12 col-sm-12 col-sm-4 col-md-3';        break;    case 5:        $block_list_col_class = 'col-12 col-sm-4 col-md-2';        break;    case 6:        $block_list_col_class = 'col-12 col-sm-4 col-md-2';        break;}?><style>    #<?= $block_id; ?> {        padding-top: <?= $block_padding_top; ?>px;        padding-bottom: <?= $block_padding_bottom; ?>px;        margin-top: <?= $block_margin_top; ?>px;        margin-bottom: <?= $block_margin_bottom; ?>px;        position: relative;        <? if (($block_background_color != '') && ($block_background_color != '#ffffff')) { ?>background-color: <?= $block_background_color; ?>;        <? } ?>    }    <? if ($block_background_image != '' && $block_background_enable) { ?><? if ($block_dark_overlay) { ?>#<?= $block_id; ?>:before {        content: '';        position: absolute;        width: 100%;        height: 100%;        top: 0;        left: 0;        background: #000;        opacity: 0.5;        z-index: 1;    }    <? } ?>#<?= $block_id; ?>>div {        position: relative;        z-index: 5;    }    #<?= $block_id; ?>:after {        content: '';        position: absolute;        width: 100%;        height: 100%;        top: 0;        left: 0;        background-image: url(<?= $block_background_image; ?>);        background-position: center center;        background-repeat: no-repeat;        background-size: cover;        z-index: 0;    }    @media only screen and (min-width: 768px) {        #<?= $block_id; ?>:after {            background-attachment: fixed;        }    }    @media only screen and (max-width: 767px) {        #<?= $block_id; ?>:after {            <?php if ($block_background_image_mobile != "") { ?>background-image: url(<?= $block_background_image_mobile; ?>);            <?php } ?>        }    }    <? } ?></style><?php$albums_query_args = array(    'numberposts'    =>  -1,    'post_type'  =>   'albums',    'post__in' => $albums,    'orderby' => 'post__in',    'order' => 'ASC',);$albums_query = get_posts($albums_query_args);$albums_ready = array();foreach ($albums_query as $albums_info) {    $albums_info_id = $albums_info->ID;    $albums_info_name = get_the_title($albums_info_id);    $albums_info_photo = get_field('albums_gall', $albums_info_id);    $albums_info_permalink = get_permalink($albums_info_id);    $albums_info_ready = array(        'id' => $albums_info_id,        'name' => $albums_info_name,        'photo' => $albums_info_photo,        'link' => $albums_info_permalink,    );    $albums_ready[] = $albums_info_ready;}// var_dump($albums_ready);?><section id="<?= $block_id; ?>" class="albums <?= ($block_white_foreground != "") ? " white-foreground " : ""; ?> ">    <div class="container">        <h2 class="text-center"><?= $block_title; ?></h2>        <p class="text-center"><?= $block_subtitle; ?></p>        <div class="albums__list row">            <?php foreach ($albums_ready as $albums_info => $albums_info_data) { ?>                <div class="col-md-3">                    <a href="<?= $albums_info_data['link'];?>" class="albums__item">                        <img src="<?= $albums_info_data['photo'][0]['sizes']['medium'];?>" alt="" class="albums__img">                        <h3 class="albums__title"><?= $albums_info_data['name'];?></h3>                        <div class="albums__counter"><?= count($albums_info_data['photo']);?> фото</div>                    </a>                </div>            <?php } ?>        </div>    </div></section>